import{_ as e,c as s,o as t,a7 as a}from"./chunks/framework.VqdLEgvg.js";const o="/assets/mem-error.BwxJmLpk.png",p="/assets/memmappedio.D643ZMe1.png",u=JSON.parse('{"title":"无法启动游戏","description":"","frontmatter":{"title":"无法启动游戏"},"headers":[],"relativePath":"faq/modern-ms/game-crash.md","filePath":"faq/modern-ms/game-crash.md","lastUpdated":1711428505000}'),r={name:"faq/modern-ms/game-crash.md"},m=a('<div class="warning custom-block"><p class="custom-block-title">错误</p><p>汉化后无法启动游戏</p><p><img src="'+o+'" alt="错误"></p></div><div class="tip custom-block"><p class="custom-block-title">解决方法</p><p>取消勾选图中的选项，才能进去游戏。</p><p><img src="'+p+'" alt="解决"></p></div><details class="details custom-block" open=""><summary>Details</summary><p>目前是需要关闭这个选项的。勾选这个并不是说游戏就会以“更快”的64位程序运行，游戏本身就是64位的，勾选不勾选都是64位。</p><p>那个选项控制的其实是读取游戏文件的方式，与是否64位无关。 目前这个功能需要关闭，是因为勾选后采用的新的读取游戏文件的方式会无法读取汉化后的数据，不勾选就是为了继续使用原来的方式读取文件。</p><p>这是国服的解释： <a href="https://mxd.web.sdo.com/web6/news/newsContent.asp?id=337186&amp;CategoryID=275" target="_blank" rel="noreferrer">https://mxd.web.sdo.com/web6/news/newsContent.asp?id=337186&amp;CategoryID=275</a></p></details>',3),c=[m];function n(i,d,_,l,f,g){return t(),s("div",null,c)}const b=e(r,[["render",n]]);export{u as __pageData,b as default};
