import{_ as s,c as t,o as a,a7 as e}from"./chunks/framework.VqdLEgvg.js";const S=JSON.parse('{"title":"四川用户无法登录","description":"","frontmatter":{"title":"四川用户无法登录"},"headers":[],"relativePath":"faq/solutions/dns-sichuan.md","filePath":"faq/solutions/dns-sichuan.md","lastUpdated":1711428505000}'),o={name:"faq/solutions/dns-sichuan.md"},c=e('<details class="details custom-block"><summary>此问题已于 2022 年 8 月 1 日解决。仅作存档。</summary><div class="warning custom-block"><p class="custom-block-title">问题</p><p>如果你在四川，可能会无法登录汉化助手。</p><p>P.S.: 原因是四川省的网络运营商对部分海外网址进行了干扰（专业词汇：DNS污染）。</p></div><div class="tip custom-block"><p class="custom-block-title">解决方法</p><p>替换掉运营商的默认DNS设置，使用其他大公司提供的DNS就可以。</p><p>阿里DNS非常详细的教程：<a href="https://alidns.com/knowledge?type=SETTING_DOCS#user" target="_blank" rel="noreferrer">点击查看教程</a></p><p>只需要配置 IPv4，请忽略 IPv6 相关的设置。</p><p>P.S.: 也可以自己上网搜索其他可用的DNS。（根据反馈，114DNS在四川也被污染）</p></div></details>',1),n=[c];function i(l,p,r,_,d,u){return a(),t("div",null,n)}const f=s(o,[["render",i]]);export{S as __pageData,f as default};
